" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Definition and diagnosis of mitral valve prolapse",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/24/13705/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/24/13705/contributors\">",
"     Sorin Pislaru, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/24/13705/contributors\">",
"     Maurice Enriquez-Sarano, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/24/13705/contributors\">",
"     Section Editor",
"    </a>",
