" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Patient assessment for air travel",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/26/13737/contributors\">",
