var title_f13_26_13739="International Neuroblastoma Risk Group staging system";
var content_f13_26_13739=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F88604&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F88604&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    International Neuroblastoma Risk Group staging system",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10\">",
"     </colgroup>",
"     <colgroup width=\"90\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        L1",
"       </td>",
"       <td>",
"        Localized tumor not involving vital structures as defined by the list of image-defined risk factors and confined to one body compartment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        L2",
"       </td>",
"       <td>",
"        Locoregional tumor with presence of one or more image-defined risk factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        M",
"       </td>",
"       <td>",
"        Distant metastatic disease (except stage MS)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        MS",
"       </td>",
"       <td>",
"        Metastatic disease in children younger than 18 months with metastases confined to skin, liver, and/or bone marrow",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Patients with multifocal primary tumors should be staged according to the greatest extent of disease as defined in the table.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     From: Monclair T, et al: J Clin Oncol 27(2), 2009:298-303. Reprinted with permission. Copyright &copy; 2009 American Society of Clinical Oncology. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
