var title_f13_26_13740="Major triatomine species of Chagas";
var content_f13_26_13740=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F73573&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F73573&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The major triatomine species that colonize the domestic and peridomestic environment and play an important role in the epidemiology of Chagas disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Vector species",
"       </td>",
"       <td class=\"subtitle1\">",
"        Found in",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Triatoma infestans",
"        </em>",
"       </td>",
"       <td>",
"        Argentina*, Brazil&bull;, Chile&bull;, Paraguay*, southern Peru, Uruguay&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Rhodnius prolixus",
"        </em>",
"       </td>",
"       <td>",
"        Colombia, El Salvador, Guatemala&Delta;, Honduras, southern Mexico, Nicaragua, Venezuela",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Triatoma dimidiata",
"        </em>",
"       </td>",
"       <td>",
"        Belize, Colombia, Costa Rica, Ecuador, El Salvador, Guatemala, Honduras, Mexico, Nicaragua, Panama, northern Peru, Venezuela",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Panstrongylus megistus",
"        </em>",
"       </td>",
"       <td>",
"        Argentina, Brazil, Paraguay, Uruguay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Triatoma brasiliensis",
"        </em>",
"       </td>",
"       <td>",
"        Northeastern Brazil",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *",
"     <em>",
"      T. cruzi",
"     </em>",
"     transmission by",
"     <em>",
"      T. infestans",
"     </em>",
"     certified as interrupted in 6 provinces of Argentina and 1 department of Paraguay",
"     <sup>",
"      [1]",
"     </sup>",
"     .",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      <em>",
"       T. cruzi",
"      </em>",
"      transmission by",
"      <em>",
"       T. infestans",
"      </em>",
"      certified as interrupted throughout the country",
"      <sup>",
"       [1]",
"      </sup>",
"      .",
"      <br>",
"       &Delta;",
"       <em>",
"        T. cruzi",
"       </em>",
"       transmission by",
"       <em>",
"        R. prolixus",
"       </em>",
"       certified as interrupted throughout the country",
"       <sup>",
"        [1]",
"       </sup>",
"       .",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reference:",
"     <br>",
"      <ol>",
"       <li>",
"        Pan American Health Organization. Chagas Disease (American Trypanosomiasis). 2010;",
"        <a href=\"file://www.paho.org/english/ad/dpc/cd/chagas.htm\">",
"         file://www.paho.org/english/ad/dpc/cd/chagas.htm",
"        </a>",
"        05/11/2010.",
"       </li>",
"      </ol>",
"      <br>",
"       Data from: WHO Expert Committee. Control of Chagas Disease. Brasilia, Brazil: World Health Organization, 2002:1-109.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
