"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gene location",
"       </td>",
"       <td class=\"subtitle1\">",
"        Protein",
"       </td>",
"       <td class=\"subtitle1\">",
"        Inheritance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pathology/ CPK",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Dystrophinopathies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Duchenne",
"       </td>",
"       <td>",
"        Xp21",
"       </td>",
"       <td>",
"        Dystrophin",
"       </td>",
"       <td>",
"        XR",
"       </td>",
"       <td>",
"        <p>",
"         Onset: 2-5 years",
"        </p>",
"        <p>",
"         Pseudohypertrophy",
"        </p>",
"        <p>",
"         Diminished IQ",
"        </p>",
"        <p>",
"         Cardiac involvement",
"        </p>",
"        <p>",
"         Rapid decline",
"        </p>",
"        <p>",
"         Death by age 20-30 years",
"        </p>",
