"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F5708915\">",
"      ALERT: U.S. Boxed Warning",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F181555\">",
"      Brand Names: U.S.",
