var title_f13_32_13836="WHO neuroendo tum lung 2004";
var content_f13_32_13836=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50502&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50502&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    2004 WHO criteria for the diagnosis of pulmonary neuroendocrine tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Tumor type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Typical carcinoid",
"       </td>",
"       <td>",
"        Carcinoid morphology and &lt;2 mitoses/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        (10 HPFs), lacking necrosis and &gt;0.5 cm",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Atypical carcinoid",
"       </td>",
"       <td>",
"        Carcinoid morphology with 2-10 mitoses/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        (10 HPFs) or necrosis (often punctuate)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Large cell neuroendocrine carcinoma",
"       </td>",
"       <td>",
"        Neuroendocrine morphology (organoid nesting palisading rosettes, trabeculae):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High mitotic rate &gt;10/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        (10 HPFs), median of 70/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        ;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Necrosis (often large zones);",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cytologic features of a NSCLC: large cell size, low nuclear to cytoplasmic ratio, vesicular or fine chromatin, and/or frequent nucleoli; some tumors have fine nuclear chromatin and lack nucleoli but qualify as NSCLC because of large cell size and abundant cytoplasm; and",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive immunohistochemical staining for one or more NE markers (other than neuron-specific enolase) and/or NE granules by electron microscopy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Small cell neuroendocrine carcinoma",
"       </td>",
"       <td>",
"        Small size (generally less than the diameter of three resting lymphocytes);",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scant cytoplasm;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nuclei: finely granular nuclear chromatin, absent or faint nucleoli;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High mitotic rate: &gt;11 mitoses/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        (10 HPFs), median of 80/2 mm",
"        <sup>",
"         2",
"        </sup>",
"        (10 HPFs); and",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frequent necrosis, often in large zones",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HPF: high-power field; NSCLC: non-small cell lung carcinoma; NE: neuroendocrine.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
