" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classifying asthma severity in youths greater than or equal to 12 years of age and adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\" rowspan=\"3\">",
"        Components of severity",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"4\">",
"        Classification of asthma severity (youths &ge;12 years of age and adults)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\" rowspan=\"2\">",
"        Intermittent",
"       </td>",
"       <td class=\"subtitle2\" colspan=\"4\">",
"        Persistent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3\">",
"        Mild",
"       </td>",
"       <td class=\"subtitle3\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle3\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        <p>",
"         Impairment",
"        </p>",
"        <p>",
"         Normal FEV",
"         <sub>",
"          1",
"         </sub>",
"         /FVC:",
"        </p>",
"        <p>",
"         8-19 yr 85 percent",
"        </p>",
"        <p>",
"         20-39 yr 80 percent",
"        </p>",
"        <p>",
"         40-59 yr 75 percent",
"        </p>",
"        <p>",
"         60-80 yr 70 percent",
"        </p>",
"       </td>",
"       <td>",
"        Symptoms",
"       </td>",
"       <td>",
"        &le;2 days/week",
"       </td>",
"       <td>",
"        &gt;2 days/week but not daily",
"       </td>",
"       <td>",
"        Daily",
"       </td>",
"       <td>",
"        Throughout the day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nighttime awakenings",
"       </td>",
"       <td>",
"        &le;2x/month",
"       </td>",
"       <td>",
"        3-4x/month",
"       </td>",
"       <td>",
"        &gt;1x/week but not nightly",
"       </td>",
"       <td>",
"        Often 7x/week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short-acting beta",
"        <sub>",
"         2",
"        </sub>",
"        -agonist use for symptom control (not prevention of EIB)",
"       </td>",
"       <td>",
"        &le;2 days/week",
"       </td>",
"       <td>",
"        &gt;2 days/week but not &gt;1x/day",
"       </td>",
"       <td>",
"        Daily",
"       </td>",
"       <td>",
"        Several times per day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interference with normal activity",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Minor limitation",
"       </td>",
"       <td>",
"        Some limitation",
"       </td>",
"       <td>",
"        Extremely limited",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lung function",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Normal FEV",
"         <sub>",
"          1",
"         </sub>",
"         between exacerbations",
"        </p>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         &gt;80 percent predicted",
"        </p>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         /FVC normal",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         &ge;80 percent predicted",
"        </p>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         /FVC normal",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         &gt;60 but &lt;80 percent predicted",
"        </p>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         /FVC reduced 5 percent",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         &lt;60 percent predicted",
"        </p>",
"        <p>",
"         &bull; FEV",
"         <sub>",
"          1",
"         </sub>",
"         /FVC reduced &gt;5 percent",
"        </p>",
"       </td>",
