var title_f13_37_13915="Ultrarush VIT";
var content_f13_37_13915=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F62051&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F62051&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Example of ultrarush venom immunotherapy schedule",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Time (minutes)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose (micrograms)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        0.1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        30",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        60",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        90",
"       </td>",
"       <td>",
"        20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        120",
"       </td>",
"       <td>",
"        30",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        150",
"       </td>",
"       <td>",
"        40",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Patients are observed for at least two hours after completion of the initial series of injections. The maintenance dose of 100 micrograms was then given on day 15 and once per month thereafter.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Schiavino D, Nucera E, Pollastrini E, et al. Specific ultrarush desensitization in Hymenoptera venom-allergic patients. Ann Allergy Asthma Immunol 2004; 92:409. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
