var title_f13_37_13916="Noninvasive evaluation of sick sinus syndrome";
var content_f13_37_13916=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F57460&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F57460&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Noninvasive evaluation of sick sinus syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Evaluation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Abnormal response",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ambulatory ECG monitoring (24 hr monitoring, event recorders, etc)",
"       </td>",
"       <td>",
"        Correlation of symptoms with a slow rate, sinus pauses or arrest, or lower pacemaker failure as well as with the bradycaria-tachycardia syndrome",
"       </td>",
"       <td>",
"        Sampling problem in that infrequent episodes may not be captured. Event recorders with memory have been more useful.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exercise testing",
"       </td>",
"       <td>",
"        Unable to reach 80 percent of maximal predicted heart rate for age and sex",
"       </td>",
"       <td>",
"        Caution in presence of ischemic heart disease. HF, pulmonary disease, orthopedic problems, drugs, and patient effort make interpretation difficult.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carotid sinus massage",
"       </td>",
"       <td>",
"        3 second or greater pause suggests a hypersensitive vasovagal and often vasodepressor response",
"       </td>",
"       <td>",
"        May occur with sick sinus syndrome or may by itself be responsible for syncope; also occurs in asymptomatic patients without SSS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Signal-averaged P wave",
"       </td>",
"       <td>",
"        Long, low amplitude signals (&lt;4 microvolts, &gt;22 msec) suggestive of SSS",
"       </td>",
"       <td>",
"        Not commonly performed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Pharmacologic interventions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Atropine (1 or 2 mg)",
"       </td>",
"       <td class=\"sublist_other\">",
"        25 percent or less increase in sinus rate or sinus rate &le;90",
"       </td>",
"       <td class=\"sublist_other\">",
"        Often equivocal results, but useful if positive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Isoproterenol (2-3 &micro;g/min)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Same as atropine",
"       </td>",
"       <td class=\"sublist_other\">",
"        Caution in presence of ischemic heart disease. Sensitivity and specificity uncertain",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Noninvasive diagnostic tests used to evaluate SA nodal function in patients with the sick sinus syndrome.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
