var title_f13_39_13949="Actions of immunosuppressives";
var content_f13_39_13949=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F78362&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F78362&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Mechanisms of action of immunosuppressive drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"6\">",
"        Glucocorticoids",
"       </td>",
"       <td>",
"        Inhibits phospholipase A2, cyclooxygenase 2, and nitric oxide",
"synthetase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits production of pro-inflammatory prostaglandins,",
"leukotrienes, and thromboxanes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits production of IL-1, IL-2, and tumor necrosis",
"factor-alpha",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits neutrophil adhesion to endothelium and chemotaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits monocyte/macrophage activation, chemotaxis, and",
"ability to recognize antigen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits T lymphocyte proliferative response to antigen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"8\">",
"        Methotrexate",
"       </td>",
"       <td>",
"        Stimulates adenosine release from",
"fibroblasts and endothelial",
"cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduces neutrophil adhesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antiproliferative effect on synovial fibroblasts and",
"endothelial cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits leukotriene B4 synthesis by neutrophils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits local IL-1 production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduces levels of IL-6 and IL-8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suppresses cell-mediated immunity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits synovial collagenase gene expression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"4\">",
"        Hydroxychloroquine",
"       </td>",
"       <td>",
"        Interferes with normal antigen",
"processing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits lysosomal enzymes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits IL-1 release",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits polymorphonuclear and lymphocyte responses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"3\">",
"        Sulfasalazine",
"       </td>",
"       <td>",
"        Inhibits polymorphonuclear cell",
"migration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduces lymphocyte responses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits angiogenesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"4\">",
"        Azathioprine",
"       </td>",
"       <td>",
"        Purine analogue; inhibts purine",
"metabolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits B- and T-cell lymphocyte functions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits B-cell proliferation and antibody synthesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits natural killer cell activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"4\">",
"        Cyclophosphamide and",
"chlorambucil",
"       </td>",
"       <td>",
"        Alkylating agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Crosslinks DNA leading to cell death",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Produces B- and T-cell lymphocytopenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibits immunoglobulin synthesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"4\">",
"        Cyclosporine and tacrolimus",
"(FK506)",
"       </td>",
"       <td>",
"        Blocks transcription of IL-2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suppresses T-cell activation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Preferential effect on CD4+ T-lymphocyte",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blocks synthesis and release of IL-1 from monocytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Etanercept",
"       </td>",
"       <td>",
"        Neutralizes soluble",
"TNF-alpha, preventing it from",
"binding to cell bound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" rowspan=\"3\">",
"        Infliximab and adalimumab",
"       </td>",
"       <td>",
"        Combine with tumor necrosis factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Down-regulate endothelial cell adhesion molecules",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreases leukocyte migration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Leflunomide",
"       </td>",
"       <td>",
"        Inhibits dihydro-orate dehydrogenase",
"which leads to depletion",
"of intracellular pyrimidine and reduces RNA synthesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Anakinra",
"       </td>",
"       <td>",
"        IL-1 receptor antagonist",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
