var title_f13_4_13387="Physiologic progression of lower-extremity rotation";
var content_f13_4_13387=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F64901&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F64901&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Normal physiologic/developmental progression of lower-extremity rotation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Normal rotation at birth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hips: Externally rotated 40&deg;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tibia: Internally rotated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Feet: Internally rotated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Developmental changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Tibial torsion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Birth: Thigh-foot angle 5&deg; lateral (external)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maturity: Thigh-foot angle 15&deg; lateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Femoral anteversion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Birth: 40&deg;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maturity: 8 to 14&deg;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
