var content_f13_4_13388=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F68104&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F68104&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Seventh edition of TNM classification of lung tumors: Changes from sixth edition",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Primary tumor (T):",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1 lesions are divided based upon size into T1a (&le;2 cm) and T1b (&gt;2 cm but &le;3 cm)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2 lesions are divided into T2a (&gt;3 cm but &le;5 cm) and T2b (&gt;5 cm but &le;7 cm)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2 tumors &gt;7 cm are reclassified as T3",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4 tumors with separate tumor nodules in the same lobe as the primary tumor are reclassified as T3",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Additional nodules in a different lobe of same lung are reclassified as T4 rather than M1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Malignant pleural or pericardial effusions or pleural nodules are now classified as metastasis (M1a) rather than T4",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Regional nodes (N):",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No changes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Metastasis (M):",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Subdivided into M1a (malignant pleural or pericardial effusion, pleural nodules, nodules in contralateral lung) and M1b (distant metastasis)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Stage grouping:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2aN1M0 lesions are classified as IIA, rather than IIB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2bN0M0 lesions are classified as IIA, rather than IB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3 (&gt;7 cm), N0M0 lesions are classified as IIB, rather than IB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3 (&gt;7 cm), N1M0 lesions are classified as IIIA, rather than IIB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3N0M0 (nodules in same lobe) lesions are classified as IIB, rather than IIIB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3N1M0 or T3N2M0 (nodules in same lobe) are classified as IIIA, rather than IIIB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4M0 (ipsilateral lung nodules) lesions are classified as IIIA (if N0 or N1) and IIIB (if N2 or N3), rather than stage IV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4M0 (direct extension) lesions are classified as IIIA (if N0 or N1), rather than IIIB",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Malignant pleural effusions (M1a) are classified as IV, rather than IIIB",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Groome, PA, Bolejack, V, Crowley, JJ, et al. J Thorac Oncol 2007; 2:694.",
"     <br>",
"      Goldstraw, P, et al. J Thorac Oncol 2007; 2:706.",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
