var title_f13_41_13981="Features of Meckels diverticulum on imaging studies";
var content_f13_41_13981=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F87461&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F87461&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Features of Meckel's diverticulum on imaging studies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Modality",
"       </td>",
"       <td class=\"subtitle1\">",
"        Features and caveats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Plain abdominal film",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           May demonstrate nonspecific signs of intestinal obstruction",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           An enterolith may be seen in the lower abdomen, but cannot be definitively localized to the Meckel's as opposed to the appendix",
"          </span>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ultrasonography",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           An obstructed Meckel&rsquo;s diverticulum may be identified as a fluid-filled pouch off the distal small intestine",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Ultrasound findings are nonspecific and are easily confused with appendicitis",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Intussusception&nbsp;can be&nbsp;diagnosed with ultrasound, but attributing the lead point to be a Meckel&rsquo;s diverticulum cannot be determined with any certainty",
"          </span>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Computed tomography",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Asymptomatic or bleeding diverticula are rarely or infrequently identified with computed tomography",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           A distinction between a Meckel&rsquo;s diverticulum and normal small intestinal loops is virtually impossible in the absence of associated inflammation",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Acutely inflamed diverticula can be identified as a blind pouch off the distal small intestine, and is typically with associated bowel wall thickening of the peri-diverticular fat",
"          </span>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Upper gastrointestinal series",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Small bowel series have demonstrated Meckel&rsquo;s diverticulum, but are unreliable even using enteroclysis in experienced hands",
"          </span>",
"         </li>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           False negative rates are high due to:",
"          </span>",
"         </li>",
"        </ul>",
"        <p>",
"         <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Rapid emptying of contrast from the diverticulum",
"         </span>",
"        </p>",
"        <p>",
"         <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp;Obscured visualization of the diverticulum due to overlying contrast-",
"         </span>",
"         <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"          filled small bowel loops",
"         </span>",
"        </p>",
"        <p>",
"         <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Poor filling of the diverticulum (occlusion or narrowing of the entrance)",
"         </span>",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mesenteric arteriorgraphy",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           A&nbsp;diagnosis of",
"           <span style=\"font-family: verdana; font-size: 13px;\">",
"            Meckel's",
"           </span>",
"           is&nbsp;based upon the finding of an anomalous superior mesenteric artery branch feeding the diverticulum; a",
"           <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"            ctive contrast extravasation may be seen in patients with ongoing hemorrhage",
"           </span>",
"          </span>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Meckel's scan",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"           Identifies areas of ectopic gastric mucosa, if present",
"          </span>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
