" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial evaluation of shock in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/49/14104/contributors\">",
