" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Ventricular septal defect in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/52/14154/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/52/14154/contributors\">",
"     Naser M Ammash, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/52/14154/contributors\">",
"     Heidi M Connolly, MD",
"    </a>",
"    <br/>",
"   </div>",
