var title_f13_54_14188="Medical eval IEI";
var content_f13_54_14188=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F77187&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F77187&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Medical evaluation for patients with symptoms of idiopathic environmental intolerance",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        1. Complete medical and environmental history; medication history. Focus on exposures at home and in the workplace",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Thorough physical examination, including neurologic examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Mental status examination and formal psychiatric evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Routine laboratory studies, which may include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Complete blood count, with differential",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Serum electrolytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Serum glucose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Blood urea nitrogen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Creatinine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Liver function tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Thyroid function tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Urinalysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Electrocardiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Chest x-ray",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Neuropsychological testing for patients with cognitive complains (eg, memory impairment), or to rule out alternate diagnoses (eg, dementia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6. Immunologic tests for patients reporting allergies or immune dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Skin tests or in vitro assays with common allergens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Immunoglobulin levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Complement levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Selected auto-antibody assays",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Simple T- and B-cell assays",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7. Other tests will be guided by the patient's complaints (eg, spirometry for patients whose primary complaints are respiratory, nerve conduction tests for patients with persistent numbness and tingling in the extremities)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
