var title_f13_55_14205="Adverse drug effects in HF";
var content_f13_55_14205=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F68838&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F68838&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs associated with increased risk of adverse effects in patients with heart failure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug/drug class",
"      </td>",
"      <td class=\"subtitle1\">",
"       Adverse effect(s)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Anti-inflammatory medications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Glucocorticoids",
"      </td>",
"      <td>",
"       Sodium retention (particularly with fludrocortisone, hydrocortisone)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       NSAIDs&bull;",
"      </td>",
"      <td>",
"       Sodium retention and peripheral vasocontriction; blunted response to diuretics and angiotensin converting enzyme inhibitors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Cardiovascular medications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Class I antiarrhythmic agents*&bull;",
"      </td>",
"      <td>",
"       Negative inotropy; proarrhythmia; increased mortality with IA and IC agents in post-MI trials",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Class III antiarrhythmic agents (included sotalol and ibutalide)*&bull;",
"      </td>",
"      <td>",
"       Proarrhythmia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Calcium channel blockers (other than amlodipine or felodipine)*&bull;",
"      </td>",
"      <td>",
"       Negative inotropy; neurohumoral activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Minoxidil*&bull;",
"      </td>",
"      <td>",
"       Sodium retention; neurohumoral activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Diabetes medications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Metformin",
"      </td>",
"      <td>",
"       Lactic acidosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Thiazolidinediones&bull;",
"      </td>",
"      <td>",
"       Sodium retention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Phosphdiesterase inhibitors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anagrelide",
"      </td>",
"      <td>",
"       Palpitations; tachycardia; sodium retention; induction or exacerbation of HF",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cilostazol",
"      </td>",
"      <td>",
"       Ventricular tachyarrhythmias",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Neurologic and psychiatric medications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Amphetamines*&bull;",
"      </td>",
"      <td>",
"       Sympathetic agonist activity; hypertension; tachycardia; tachyarrhythmias",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Carbamazepine",
"      </td>",
"      <td>",
"       Negative inotropic effect; bradyarrhythmias",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clozapine",
"      </td>",
"      <td>",
"       Development of myocarditis and cardiomyopathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ergot alkaloids (ergotamine, dihydroergotamine)",
"      </td>",
"      <td>",
"       Sympathetic agonist activity; valve fibrosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pergolide (not available in the US)",
"      </td>",
"      <td>",
"       Valve fibrosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Tricyclic antidepressants",
"      </td>",
"      <td>",
"       Negative inotropic effect; proarrhythmia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Miscellaneous",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Beta-2 agonists",
"      </td>",
"      <td>",
"       Sympathetic agonist activity; tachyarrhythmias; hypokalemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Itraconazole",
"      </td>",
"      <td>",
"       Negative inotropic effect; also can increase serum digoxin concentrations",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Licorice",
"      </td>",
"      <td>",
"       Mineralocorticoid excess: sodium retention and hypokalemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Sodium-containing preparations (eg, Fleet PhosPho soda)",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Sodium load QT-prolonging drugs (eg, haloperidol, erythromycin)",
"      </td>",
"      <td>",
"       Proarrhythmia",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     NSAIDs: nonsteroidal anti-inflammatory drugs; SVR: systemic vascular resistance; HF: heart failure.",
"     <br>",
"      * Evidence from randomized placebo controlled trials.",
"      <br>",
"       &bull; Increased risk noted in American College of Cardiology/American Heart Association heart failure guidelines (Hunt SA, et al. Circulation 2005; 112:e154.)",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data modified from Amabile CM, Spencer AP. Arch Intern Med 2004; 164:709.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
