" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations and diagnosis of Chagas heart disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/59/14266/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/59/14266/contributors\">",
"     J Antonio Marin-Neto, MD, PhD, FACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/59/14266/contributors\">",
"     Benedito C Maciel, MD, FACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/59/14266/contributors\">",
"     Marcus V Sim&otilde;es, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/59/14266/contributors\">",
"     Andre Schmidt, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/59/14266/contributors\">",
