var title_f13_59_14267="Contact lens care tips";
var content_f13_59_14267=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F50775&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F50775&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contact lens care tips",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        &bull; Follow recommended wearing schedule.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Do",
"        <strong>",
"         not",
"        </strong>",
"        substitute sterile saline solutions for multi-purpose solutions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Rub and rinse your contact lenses as directed by your eye care professional.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Do not \"top-off\" the solutions in your case. Always discard all of the leftover contact lens solution after each use. Never reuse any lens solution.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Clean, rinse and air-dry your lens case each time lenses are removed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Do not expose your contact lenses to any water: tap, bottled, distilled, lake or ocean water.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Contact your eye care professional if you experience any symptoms of eye irritation or infection.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: US Food and Drug Administration, Medical Devices, Contact Lenses. Available at: www.fda.gov/cdrh/contactlenses/lenslist.html (Accessed on August 20th, 2009).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
