var title_f13_59_14268="Treatment of VWD";
var content_f13_59_14268=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F75956&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F75956&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of von Willebrand disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Medication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Desmopressin (dDAVP)",
"       </td>",
"       <td>",
"        <p>",
"         IV: 0.3 &micro;g/kg in 50 mL saline over 20 min.",
"        </p>",
"        <p>",
"         Nasal spray: weight &gt;50 kg: 300 &micro;g (1 spray in each nostril); &lt;50 kg: 150 &micro;g (1 spray in one nostril)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Useful in most patients with type 1; variable in type 2;* Not useful in type 3.",
"        </p>",
"        <p>",
"         Patient should have therapeutic trial to measure response before use.",
"        </p>",
"        <p>",
"         May repeat dose after 12 hr and 24 hr. Tachyphylaxis and hyponatremia may occur; need to monitor patient.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        VWF concentrates containing all VWF multimers",
"       </td>",
"       <td>",
"        <strong>",
"         Major bleeding or surgery:",
"        </strong>",
"        Initial dose 40-60 ristocetin cofactor units per kg followed by 20-40 ristocetin cofactor units per kg q 12-24 hr to keep VWF level 50-100 IU/dL for 7-14 days.",
"        <br/>",
"        <strong>",
"         Minor bleeding or surgery:",
"        </strong>",
"        Initial dose 30-60 ristocetin cofactor units per kg followed by 20-40 ristocetin cofactor units per kg q 12-48 hr to keep VWF level &gt;30 IU/dL for 3-5 days.",
"       </td>",
"       <td>",
"        Dose and duration based on clinical experience.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Antifibrinolytic agents:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Aminocaproic acid",
"       </td>",
"       <td class=\"sublist_other\">",
"        25-50 mg/kg (maximum 5 g dose) 4 x daily by mouth",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"2\">",
"        Use alone or in conjunction with other therapy. Especially useful for mucosal bleeding (often for dental procedures).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Tranexamic acid",
"       </td>",
"       <td class=\"sublist_other\">",
"        10 mg/kg 3 x daily IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IVIG (immune acquired inhibitors of VWF)",
"       </td>",
"       <td>",
"        1 g/kg daily for 2 days - infusion over 8-12 hr",
"       </td>",
"       <td>",
"        Use after trial of DDAVP or other measures in patients with acquired VWD, particularly when associated with autoimmune diseases.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Thrombocytopenia may worsen in some type 2B patients.",
"     <br>",
"      &bull; Oral preparation not available in the United States.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Rick, ME, Med Clin North Am 1994; 78:609. Copyright 1994 WB Saunders.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
