var title_f13_62_14315="Diagnosis Munchausen by proxy";
var content_f13_62_14315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F65683&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F65683&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Meadow's guidelines for diagnosis Munchausen syndrome by proxy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Study the history to determine which events are real and which are fabricated",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Look for temporal associations between illness events and the mother's behavior",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Scrutinize the personal, social, and family history that the mother has given",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Contact other family members",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Contact the mother's physician about a possible history of Munchausen's or unexplained illnesses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ensure that the hospital laboratory stores samples from the child for possible future screenings",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       More carefully monitor mother and child, possibly by video surveillance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider a search of the mother and her possessions for possible poisons or substances",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Most important, exclude the mother for a day or two and observe whether the symptoms disappear",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Meadow, R, Arch Dis Childhood 1982; 57:92.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
