var title_f13_62_14316="Neuro complications of hyperthy";
var content_f13_62_14316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F60148&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F60148&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of the more common neurologic manifestations of hyperthyroidism and Graves' disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cardinal features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Relative frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cognitive dysfunction",
"       </td>",
"       <td>",
"        Ranges from mild inattention and anxiety to agitated delirium, rarely coma",
"       </td>",
"       <td>",
"        Common, especially with acute and more severe thyrotoxicosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seizures",
"       </td>",
"       <td>",
"        Usually generalized tonic clonic seizures that usually occur with encephalopathy in acute thyrotoxicosis",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tremor",
"       </td>",
"       <td>",
"        High frequency, low amplitude",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chorea",
"       </td>",
"       <td>",
"        Unifocal or multifocal, sustained or paroxysmal",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stroke",
"       </td>",
"       <td>",
"        Usually cardioembolic, associated with thyrotoxic-induced atrial fibrillation",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myopathy",
"       </td>",
"       <td>",
"        Usually mild proximal muscle weakness with normal serum CK",
"       </td>",
"       <td>",
"        Common, but may be subclinical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polyneuropathy",
"       </td>",
"       <td>",
"        <p>",
"         Usually an axonal sensory polyneuropathy",
"        </p>",
"        <p>",
"         Rare demyelinating polyneuropathy",
"        </p>",
"       </td>",
"       <td>",
"        Approximately 25 percent, but may be subclinical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myasthenia gravis",
"       </td>",
"       <td>",
"        Perhaps more likely to be ocular rather than generalized",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Periodic paralysis",
"       </td>",
"       <td>",
"        Usually associated with hypokalemia",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Graves' ophthalmopathy",
"       </td>",
"       <td>",
"        Proptosis, restricted eye movements",
"       </td>",
"       <td>",
"        Approximately 25 percent of patients with Graves' disease",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
