var title_f13_63_14331="Dx possible MSA";
var content_f13_63_14331=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F81150&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F81150&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for possible MSA",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        A sporadic, progressive, adult (&gt;30 y)-onset disease characterized by:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Parkinsonism (bradykinesia with rigidity, tremor, or postural instability)",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A cerebellar syndrome (gait ataxia with cerebellar dysarthria, limb ataxia, or cerebellar oculomotor dysfunction)",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        At least one feature suggesting autonomic dysfunction (otherwise unexplained urinary urgency, frequency or incomplete bladder emptying, erectile dysfunction in males, or significant orthostatic blood pressure decline that does not meet the level required in probable MSA)",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        At least one of the additional features shown in Table 3",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MSA: multiple system atrophy; y: year.",
"    </div>",
"    <div class=\"reference\">",
"     Gilman, S, Wenning, GK, Low, PA, et al. Second consensus statement on the diagnosis of multiple system atrophy. Neurology 2008; 71:670.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
