var title_f13_8_13453="Second line TB drugs for adults";
var content_f13_8_13453=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F66493&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F66493&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dosing of second line antituberculosis drugs in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup span=\"3\" width=\"30%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Daily adult dosage*",
"        <br/>",
"        (normal renal function)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Main adverse affects",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levofloxacin",
"       </td>",
"       <td>",
"        1000 mg orally or IV",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        GI toxicity, CNS effects, rash, dysglycemia, tendonitis or tendon rupture, QT prolongation",
"       </td>",
"       <td>",
"        Potential choice when there are no suitable alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moxifloxacin",
"       </td>",
"       <td>",
"        400 mg orally or IV",
"       </td>",
"       <td>",
"        GI toxicity, CNS effects, rash, tendonitis or tendon rupture, QT prolongation, dysglycemia",
"       </td>",
"       <td>",
"        Potential choice when there are no suitable alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Capreomycin",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        15 mg/kg IM or IV (max 1 g)",
"       </td>",
"       <td>",
"        Auditory and vestibular toxicity, nephrotoxicity, electrolyte disturbances",
"       </td>",
"       <td>",
"        Avoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kanamycin",
"        <sup>",
"         &Delta;&loz;",
"        </sup>",
"       </td>",
"       <td>",
"        15 mg/kg IM or IV (max 1 g)",
"       </td>",
"       <td>",
"        Ototoxicity, nephrotoxicity",
"       </td>",
"       <td>",
"        Avoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amikacin",
"        <sup>",
"         &Delta;&loz;",
"        </sup>",
"       </td>",
"       <td>",
"        15 mg/kg IM or IV (max 1 g)",
"       </td>",
"       <td>",
"        Ototoxicity, nephrotoxicity",
"       </td>",
"       <td>",
"        Avoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Streptomycin",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        15 mg/kg IM or IV (max 1 g)",
"       </td>",
"       <td>",
"        Vestibular and ototoxicity, neurotoxicity, nephrotoxicity",
"       </td>",
"       <td>",
"        Avoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ethionamide",
"       </td>",
"       <td>",
"        15 to 20 mg/kg orally as a single daily dose or 2 divided doses (max 500 mg twice daily)",
"       </td>",
"       <td>",
"        <p>",
"         GI and hepatic toxicity, neurotoxicity, hypothyroidism, optic neuritis.",
"        </p>",
"        Pyridoxine 50 to 100 mg orally per day may be useful in preventing or reducing neurotoxicity.",
"       </td>",
"       <td>",
"        Potential choice when there are no suitable alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cycloserine",
"       </td>",
"       <td>",
"        10 to 15 mg/kg orally in 2 divided doses (max 500 mg twice daily)",
"       </td>",
"       <td>",
"        <p>",
"         Psychiatric symptoms, headaches, seizures.",
"        </p>",
"        Pyridoxine 50 mg (oral once per day) for every 250 mg of cycloserine may be useful in preventing or reducing neurotoxicity.",
"       </td>",
"       <td>",
"        Potential choice when there are no suitable alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Para-aminosalicylic acid",
"       </td>",
"       <td>",
"        8 to 12 g orally in 2 or 3 divided doses",
"       </td>",
"       <td>",
"        GI toxicity, malabsorption, hypersensitivity, hepatitis, hypothyroidism",
"       </td>",
"       <td>",
"        Potential choice when there are no suitable alternatives",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IM: intramuscular; IV: intravenous; GI: gastrointestinal; CNS: central nervous system; max: maximum.",
"     <br/>",
"     * Dosage may need to be adjusted for renal impairment.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Greater efficacy has been observed with administration of 1000 mg (compared with 500 mg) for treatment of multidrug-resistant tuberculosis.",
"     <br/>",
"     &Delta; Generally given 5 to 7 times per week (15 mg/kg, or a maximum of 1 g per dose) for an initial 2 to 4 months, and then (if needed) 2 to 3 times per week (20 to 30 mg/kg, or a maximum of 1.5 g per dose). For patients &gt;59 years old, dosage is reduced to 10 mg/kg (max 750 mg per dose). Dosage should be decreased if renal function is diminished.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     For patients who are overweight or obese, dose is based on ideal body weight or dosing weight (see UpToDate calculator). Adjust dose based on serum concentration monitoring for target trough &lt;1 mcg/mL and target peak of 56 to 64 mcg/mL for once daily administration.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with special permission from: Treatment Guidelines from The Medical Letter, April 2012; Vol. 10 (116):29.",
"     <a href=\"file://www.medicalletter.org\" target=\"_blank\">",
"      www.medicalletter.org",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
