var title_f14_0_14347="Management of myxedema coma";
var content_f14_0_14347=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F61507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F61507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of myxedema coma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Draw serum for T4, TSH, and cortisol.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer thyroxine 200 to 400 &micro;g (0.2 to 0.4 mg)",
"intravenously followed by daily doses of 50 to 100 &micro;g, and",
"triiodothyronine 5 to 20 &micro;g intravenously followed by 2.5 to",
"10 &micro;g every 8 hours.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Change to an equivalent oral dose of thyroxine when the patient can",
"tolerate oral medications. (Oral dose = intravenous dose &divide; 0.75).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Supportive measures:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Mechanical ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Fluids",
"and vasopressor drugs to correct hypotension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Passive rewarming",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Intravenous dextrose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Stress-doses glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Consider empirical",
"antibiotic treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Monitor for arrhythmias and treat when indicated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
