" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Neurological examination of the newborn",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Suresh Kotagal, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Joseph A Garcia-Prats, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Douglas R Nordli, Jr, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/10/14505/contributors\">",
"     Deputy Editor",
"    </a>",
