var title_f14_12_14540="Malformations Fanconi anemia";
var content_f14_12_14540=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F71159&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F71159&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common physical anomalies in children with Fanconi anemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Anomaly",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency (percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any physical abnormality",
"       </td>",
"       <td>",
"        60",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin - generalized hyperpigmentation; hypopigmented areas; large freckles, caf&eacute;-au-lait spots",
"       </td>",
"       <td>",
"        40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short stature, delicate features",
"       </td>",
"       <td>",
"        40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper limbs - absent or hypoplastic thumb, supernumerary, bifid, clinodactyly",
"       </td>",
"       <td>",
"        35",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gonads - hypogenitalia, undescended/absent testes, hypospadias; bicornuate or malpositioned uterus",
"       </td>",
"       <td>",
"        <p>",
"         25 (males)",
"        </p>",
"        <p>",
"         2 (females)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Head - microcephaly or hydrocephaly; birdlike face, mid-face hypoplasia, Sprengel's deformity of neck",
"       </td>",
"       <td>",
"        20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eyes - microophthalmia, ptosis, epicanthal folds, strabismus",
"       </td>",
"       <td>",
"        20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kidney - abnormal, ectopic, horseshoe, hypoplastic or absent kidney, hydronephrosis",
"       </td>",
"       <td>",
"        20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ears - deafness (usually conductive); low set ears, abnormal shape, narrow canal",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Developmental delay",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiopulmonary anomalies - congenital heart disease (patent ductus arteriosus, atrial or ventricular septal defects, coarctation, situs inversus)",
"       </td>",
"       <td>",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gastrointestinal anomalies - atresias, imporforate anus, tracheoesophageal fistula, malrotation",
"       </td>",
"       <td>",
"        5",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Alter BP, Young NS. Bone marrow failure syndromes. In: Hematology of Infancy and Childhood, Nathan DG, Orkin SH (Eds), WB Saunders, Philadelphia, 1998, p.238. Also from: Shimamura A, Alter BP. Pathophysiology and management of inherited bone marrow failure syndromes. Blood Reviews 2010; 24:101.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
