" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of occupational and environmental health",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/13/14553/contributors\">",
