" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial management of distal forearm fractures in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Paula Schweich, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Jennifer Wang, DO",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Richard G Bachur, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/14/14569/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
