var title_f14_14_14572="PegIFN Rx child HCV";
var content_f14_14_14572=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F61219&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F61219&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Combination therapy with pegylated interferon and ribavirin for children 3 years and older with compensated HCV liver disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Peginterferon*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pegylated interferon alfa-2b (PegIntron&reg;), 60 micrograms/m",
"        <sup>",
"         2",
"        </sup>",
"        subcutaneously once per week (maximum dose 1.5 micrograms/kg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -- OR --&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pegylated interferon alfa-2a (Pegasys&reg;), 180 micrograms/1.73 m",
"        <sup>",
"         2",
"        </sup>",
"        once per week (maximum dose 180 micrograms)",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        and Ribavirin (Rebetol&reg; or Copegus&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        15 mg/kg/day orally with food, divided into 2 doses",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Available as 200 mg capsules or 40 mg/ml oral solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Duration of therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        48 weeks for genotype 1",
"        <sup>",
"         &loz;&sect;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        24 weeks may be sufficient for genotype 2 or 3",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If depression develops, peginterferon should be given at a reduced dose (mild or moderate depression) or discontinued (severe depression).",
"     <br>",
"      &bull;&nbsp;For pegylated interferon alfa-2a&nbsp;(Pegasys&reg;), safety and efficacy have not been established for children younger than five years.&nbsp;",
"      <br>",
"       &Delta; If anemia develops, ribavirin should be given at a reduced dose (anemia alone), and discontinued (pancytopenia) (see package insert for parameters).",
"       <br>",
"        &loz;&nbsp;Earlier discontinuation of therapy is suggested if patients do not achieve at least a 2 log decrease in or loss of HCV-RNA after 12 weeks of therapy, or if HCV-RNA is detectable after 24 weeks of treatment (because these patients are unlikely to achieve sustained virologic response).",
"        <br>",
"         &sect; If possible, treatment of children with HCV genotype 1 might be deferred until triple therapy is available for children, or done sooner if under the auspices of a clinical trial",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from prescribing information for PegIntron&reg; package insert, approved 3/10/09; and for Pegasys&reg;, approved 8/2011.&nbsp;Available at: Drugs@FDA.gov.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
