" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pharmacology of drugs used to treat attention deficit hyperactivity disorder in children and adolescents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Kevin R Krull, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Marilyn Augustyn, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/19/14650/contributors\">",
"     Mary M Torchia, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
