var title_f14_2_14379="Assessing menstrual blood loss";
var content_f14_2_14379=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F69390&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F69390&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Questions to ask to help quantify blood loss during menses",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        How often do you change your sanitary pad/tampon during peak flow days?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How many pads/tampons do you use over a single menstrual period?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do you need to change the pad/tampon during the night?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How large are any clots that are passed?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Has a medical provider told you that you are anemic?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Women with a normal volume of menstrual blood loss tend to:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        change pads/tampons at &ge;3 hour intervals,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        use fewer than 21 pads/tampons per cycle,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        seldom need to change the pad/tampon during the night,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        have clots less than 1 inch in diameter,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        not be anemic",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Warner, PE, Critchley, HD, Lumsden, MA, et al. Am J Obstet Gynecol 2004; 190:1216.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
