var title_f14_2_14380="Clin features diaper derm";
var content_f14_2_14380=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F62319&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F62319&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features in the differential diagnosis of diaper dermatitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Etiology",
"      </td>",
"      <td class=\"subtitle1\">",
"       Clinical features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Irritant",
"      </td>",
"      <td>",
"       Lesions range from erythema to painful scaling papules and superficial erosions on convex surfaces in direct contact with the diaper; typically spares skin folds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Jacquet's diaper dermatitis",
"      </td>",
"      <td>",
"       Well-demarcated papules and erosions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Granuloma gluteal infantum",
"      </td>",
"      <td>",
"       Reddish-purple nodules in inguinal folds, scrotum, buttocks, and medial thighs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Candidal",
"      </td>",
"      <td>",
"       Beefy red plaques, satellite papules, superficial pustules in skin folds but may also affect convex surfaces",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Allergic contact",
"      </td>",
"      <td>",
"       Well-demarcated erythema, scaly papules and plaques in a characteristic pattern (eg, \"cowboy holster\" or in the distribution of diaper dye); pruritic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Seborrhea",
"      </td>",
"      <td>",
"       Well-circumscribed erythematous papules and plaques with greasy yellow scale most prominent in the skin folds; rarely isolated to diaper area",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Impetigo",
"      </td>",
"      <td>",
"       Pustules that rupture, leaving a collarette of scale; honey-colored crusted erosions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Group A streptococcus",
"      </td>",
"      <td>",
"       Bright red sharply demarcated perianal or perineal rash",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Psoriasis",
"      </td>",
"      <td>",
"       Sharply demarcated erythematous scaly papules and plaques; scale may be absent in diaper area; possible family history of psoriasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Langerhans cell histiocytosis",
"      </td>",
"      <td>",
"       Red-orange or yellow-brown scaly papules, erosions, or petechiae in the groin, intertriginous regions, and scalp; may have systemic involvement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Scabies",
"      </td>",
"      <td>",
"       Erythematous papules, nodules, and excoriations; pruritus; other family members often affected",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Congenital syphilis",
"      </td>",
"      <td>",
"       Copper-colored scaly macules and papules or moist erosions in diaper area and/or around the mouth and nose",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
