var title_f14_2_14381="MAP chemo osteosarcoma";
var content_f14_2_14381=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F62611&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F62611&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Doxorubicin, cisplatin, and high-dose methotrexate (MAP) followed by surgery and MAP for nonmetastatic osteosarcoma (control arm, COG AOST 0331)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Weeks",
"       </td>",
"       <td class=\"subtitle1\">",
"        Agents",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Induction MAP (weeks 1 through 10)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        1, 6",
"       </td>",
"       <td>",
"        Doxorubicin",
"       </td>",
"       <td>",
"        37.5 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day by continuous IV infusion or IV push",
"       </td>",
"       <td rowspan=\"2\">",
"        1 and 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cisplatin",
"       </td>",
"       <td>",
"        60 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day IV over four hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        4, 5, 9, 10",
"       </td>",
"       <td>",
"        High-dose methotrexate",
"       </td>",
"       <td>",
"        12 grams/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over four hours*",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leucovorin rescue",
"       </td>",
"       <td>",
"        15 mg (approximately 10 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        ) every six hours IV or orally for 10 doses&bull;",
"       </td>",
"       <td>",
"        Starting 24 hours after beginning high-dose methotrexate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Surgery (week 11)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        11",
"       </td>",
"       <td colspan=\"3\">",
"        Resection or amputation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Postoperative MAP (weeks 12 through 29)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        12, 17",
"       </td>",
"       <td>",
"        Doxorubicin&Delta;",
"       </td>",
"       <td>",
"        37.5 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day by continuous IV&nbsp;infusion or IV push",
"       </td>",
"       <td rowspan=\"3\">",
"        1 and 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cisplatin",
"       </td>",
"       <td>",
"        60 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day IV over four hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        22, 26",
"       </td>",
"       <td>",
"        Doxorubicin&Delta;",
"       </td>",
"       <td>",
"        37.5 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day IV over 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        15, 16, 20, 21, 24, 25, 28, 29",
"       </td>",
"       <td>",
"        High-dose methotrexate",
"       </td>",
"       <td>",
"        12 grams/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over four hours",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leucovorin rescue",
"       </td>",
"       <td>",
"        15 mg (approximately 10 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        ) every six hours IV or orally for 10 doses",
"       </td>",
"       <td>",
"        Starting 24 hours after beginning high-dose methotrexate",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Some pediatric oncologists limit the methotrexate dose to 20 grams total (Meyers, PA, et al. J Clin Oncol 2005; 23:2004).",
"     <br>",
"      &bull;&nbsp;Leucovorin doses adjusted depending on blood methotrexate levels.",
"      <br>",
"       &Delta; Dexrazoxane can be given with doxorubicin, although osteosarcoma-specific data are not available.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Created with data from:",
"     <br>",
"      <ol>",
"       <li>",
"        National Cancer Institute Clinical Trials. Phase III randomized study of neoadjuvant induction therapy comprising doxorubicin, cisplatin, and high-dose methotrexate (MAP) followed by surgery and adjuvant maintenance therapy comprising map alone versus map and peg-interferon alfa-2b or MAP combined with ifosfamide and etoposide in patients with resectable high-grade osteosarcoma; available at:&nbsp;",
"        <a href=\"file://www.cancer.gov/clinicaltrials/search/view?cdrid=438714&amp;version=HealthProfessional\" style=\"FONT-STYLE: italic\">",
"         file://www.cancer.gov/clinicaltrials/search/view?cdrid=438714&amp;version=HealthProfessional",
"        </a>",
"        (accessed June 15, 2012).",
"       </li>",
"       <li>",
"        Goorin AM, Schwartzentruber DJ, Devidas M, et al. Presurgical chemotherapy compared with immediate surgery and adjuvant chemotherapy for nonmetastatic osteosarcoma: Pediatric Oncology Group Study POG-8651. J Clin Oncol 2003; 21:1574.",
"       </li>",
"       <li>",
"        Geller DS, Goorlick R. Osteosarcoma: a review of diagnosis, management, and treatment strategies. Clin Adv Hematol Oncol 2010; 8:705.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
