var title_f14_20_14667="Long term outcomes in infantile spasms";
var content_f14_20_14667=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F78369&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F78369&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Key Key outcome parameters in West syndrome/Infantile spasms (irrespective of cause)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Suppression of spasms (in response to treatment)",
"       </td>",
"       <td>",
"        50 to 90 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Development of other seizure types",
"       </td>",
"       <td>",
"        50 to 70 percent (intractable in 40 to 50 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evolution into the Lennox-Gastaut syndrome",
"       </td>",
"       <td>",
"        20 to 50 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Learning difficulties (usually moderate or severe)",
"       </td>",
"       <td>",
"        70 to 90 percent*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psychiatric (including autistic) dysfunction",
"       </td>",
"       <td>",
"        20 to 40 percent*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mortality",
"       </td>",
"       <td>",
"        5 to 30 percent (of which 50 percent are disease, and 50 percent treatment-related)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Appleton, RE. West syndrome: long-term prognosis and social aspects. Brian and Development 2001; 23:688. Copyright &copy; 2001 Elsevier Science",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
