var title_f14_23_14715="Dosing and administration of tetracyclines";
var content_f14_23_14715=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F80309&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F80309&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dosing and administration of tetracyclines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"        Drug",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Doxycycline",
"       </td>",
"       <td>",
"        100 mg IV/PO every 12 hours; No adjustment in renal dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Tetracycline",
"       </td>",
"       <td>",
"        250-500 mg PO every 6 hours with normal renal function.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        250-500 mg PO every 12 to 24 hours with CrCl 10-50 mL/min.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not recommended when CrCl &lt;10 mL/min",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minocycline",
"       </td>",
"       <td>",
"        100 mg PO every 12 hours; No adjustment in renal dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Demeclocycline",
"       </td>",
"       <td>",
"        150-300 mg PO every 12 to 24 hours; Avoid use in renal dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tigecycline",
"       </td>",
"       <td>",
"        100 mg initial dose then 50 mg IV every 12 hours; No adjustment in renal dysfunction",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
