var title_f14_24_14731="Signs and symptoms of severe preeclampsia";
var content_f14_24_14731=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F78675&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F78675&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Signs and symptoms of severe preeclampsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        New onset high blood pressure AND proteinuria, in addition to at least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blurred vision, scotomata, altered mental status, severe headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Right upper quadrant or epigastric pain, nausea, vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver enzymes at twice normal levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Severe blood pressure elevation:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Systolic blood pressure &ge;160 mm Hg or diastolic &ge;110 mm Hg on two occasions at least six hours apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less than 100,000 platelets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe proteinuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased urine output (&lt; 500 mL in 24 hours)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intrauterine fetal growth restriction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fluid in the lungs (pulmonary edema) or bluish discoloration of the skin (called cyanosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stroke",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
