" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment and prevention of schistosomiasis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/29/14807/contributors\">",
