" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Bacterial arthritis: Clinical features and diagnosis in infants and children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/30/14826/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/30/14826/contributors\">",
"     Paul Krogstad, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/30/14826/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/30/14826/contributors\">",
"     Morven S Edwards, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/30/14826/contributors\">",
"     William Phillips, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/30/14826/contributors\">",
"     Robert Sundel, MD",
"    </a>",
"    <br/>",
"   </div>",
