var title_f14_33_14877="Causes S2 abnormalities";
var content_f14_33_14877=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F77038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F77038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Abnormalities of the second heart sound (S2) and their mechanisms",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Wide splitting of S2 with maintained inspiratory delay of P2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Delayed activation and completion of right ventricular ejection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Complete right bundle branch block",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Artificial left ventricular pacing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pre-excitation of the left ventricle (Wolff-Parkinson-White syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Premature beats and idioventricular rhythm originating from the left ventricle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Prolonged right ventricular ejection time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulmonary hypertension with right heart failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Right ventricular outflow obstruction (eg, pulmonic stenosis, acute massive pulmonary embolus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Increased pulmonary hangout time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Idiopathic dilatation of the pulmonary artery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mild pulmonic stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Postoperative atrial septal defect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Decreased left ventricular ejection time (early A2)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mitral regurgitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ventricular septal defect with low pulmonary vascular resistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Constrictive pericarditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Fixed splitting of S2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unchanged right ventricular stroke volume during respiration; severe right ventricular failure due to any cause",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interatrial communication; atrial septal defect; common atrium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Reversed splitting of S2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Delayed left ventricular activation and completion of ejection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Left bundle branch block",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Artificial right ventricular pacing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pre-excitation of the right ventricle (Wolff-Parkinson-White syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Premature beats of right ventricular origin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Prolonged left ventricular ejection time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Increased resistance to left ventricular ejection (aortic stenosis, obstructive hypertrophic cardiomyopathy, hypertension)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Isolated increase in left ventricular forward stroke volume (aortic regurgitation, patent ductus arteriosus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Myocardial dysfunction (mild to moderate left ventricular failure, myocardial ischemia or infarction)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Increased aortic hangout time (not the sole cause)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aortic regurgitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patent ductus arteriosus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aortic stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Single S2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Apparent:",
"        </strong>",
"        obesity, emphysema, pericardial effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Absent A2:",
"        </strong>",
"        severe aortic stenosis, severe aortic regurgitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Absent P2:",
"        </strong>",
"        absent pulmonary valve, pulmonary atresia, tetralogy of Fallot, truncus arteriosus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Fusion of A2 and P2:",
"        </strong>",
"        Eisenmenger's ventricular septal defect, common ventricle",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
