var title_f14_35_14908="Amelogenesis imperfecta";
var content_f14_35_14908=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F66836&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F66836&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical and hereditary characteristics of amelogenesis imperfecta",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical appearance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Enamel thickness",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic appearance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Inheritance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoplastic",
"        <br/>",
"        (type 1)",
"       </td>",
"       <td>",
"        Crown size varies from small to normal, small teeth may lack proximal contacts, color varies from normal to opaque white/yellow-brown",
"       </td>",
"       <td>",
"        Varies from thin and smooth to normal thickness with grooves, furrows, or pits",
"       </td>",
"       <td>",
"        Enamel has normal to slightly reduced contrast, thin",
"       </td>",
"       <td>",
"        Autosomal dominant, recessive, or X-linked",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Hypomaturation",
"        <br/>",
"        (type 2)",
"       </td>",
"       <td>",
"        Varies from creamy opaque to marked yellow or brown, surface of teeth soft and rough, dental sensitivity and open bite common",
"       </td>",
"       <td>",
"        Normal thickness with enamel that often chips and abrades easily",
"       </td>",
"       <td>",
"        Enamel has contrast similar to dentin, unerupted crowns have normal morphology",
"       </td>",
"       <td>",
"        Autosomal dominant, recessive, or X-linked",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Hypocalcified",
"        <br/>",
"        (type 3)",
"       </td>",
"       <td>",
"        Opaque white to yellow-brown, soft rough enamel surface, dental sensitivity and open bite common, heavy calculus formation common",
"       </td>",
"       <td>",
"        Normal thickness with enamel that often chips and abrades easily",
"       </td>",
"       <td>",
"        Enamel has contrast similar to or &lt; dentin, unerupted crowns have normal morphology",
"       </td>",
"       <td>",
"        Autosomal dominant, recessive",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Hypomaturation/ hypoplasia/ taurodomism",
"        <br/>",
"        (type 4)",
"       </td>",
"       <td>",
"        White/yellow-brown mottled, teeth can appear small and lack proximal contact",
"       </td>",
"       <td>",
"        Reduced, hypomineralized areas and pits",
"       </td>",
"       <td>",
"        Enamel contrast normal to slightly &gt; dentin, large pulp chambers",
"       </td>",
"       <td>",
"        Autosomal dominant",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Wright JT. Pediatr Clin North Am 2000; 47:986. Copyright 2000 W.B. Saunders Company.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
