" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Linear IgA bullous dermatosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/36/14922/contributors\">",
