var title_f14_37_14939="Nebulizer technique";
var content_f14_37_14939=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71696&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71696&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Technique for use of medication nebulizer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Place patient in a comfortable position (preferably sitting up or partially supine, since there is some risk of spillage if the patient is lying flat).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assemble apparatus.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Add medication to nebulizer.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use a fill volume of 3 to 6 mL.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Attach a&nbsp;compressor or a pressurized gas supply&nbsp;(eg, compressed air or oxygen) with a flow of 6 to 8 L/min.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Instruct patient to breathe through the mouth whether using a mask or mouthpiece. If using a mouthpiece, the patient can rest teeth on mouthpiece and close lips around it.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encourage patient to breathe with a slow inspiratory flow and an occasional deep breath.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Periodically tap nebulizer to return impacted&nbsp;droplets to reservoir.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stop&nbsp;treatment when the nebulizer sputters despite tapping.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
