var title_f14_38_14955="Allergen avoidance restaurants";
var content_f14_38_14955=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F76289&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F76289&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential allergen avoidance pitfalls in restaurant meals",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Shared food preparation (blender, pan, fryer, grill, etc)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cross contact of primary food with allergen (eg, vanilla ice cream previously contacted with peanut ice cream from prior scoop, vegetable tray containing walnut from a prior spill)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor communication between consumer and chef",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hidden or \"secret ingredients\" (allergen in sauces, powder forms)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cross contact during serving (buffet, preparation trays, garnish, spills)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Addition of allergen after final preparation (added butter, etc)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Removal of ingredient with residual allergen (removing nuts from a salad)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Foods prepared in advance and ingredients unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allergen used without notion of \"ingredient\" (peanut butter to seal an egg roll, nuts for decoration)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
