var title_f14_38_14956="Vaccine schedule age 7 to 18 years PI";
var content_f14_38_14956=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F73404&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F73404&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommended vaccine schedule for children age 7 to 18 years (United States, 2013)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"20%\">",
"     </colgroup>",
"     <colgroup width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Vaccine",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Notes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7 to 18 years",
"       </td>",
"       <td>",
"        Influenza (flu)",
"       </td>",
"       <td>",
"        1 dose each year",
"       </td>",
"       <td>",
"        Children get 1 dose each fall. Children younger than 9 years who are getting the flu vaccine for the first time need 2 doses, given at least 1 month apart.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        11 to 12 years",
"       </td>",
"       <td>",
"        Tetanus, diphtheria, pertussis (Tdap booster)",
"       </td>",
"       <td>",
"        1 of 1",
"       </td>",
"       <td>",
"        This is usually given at age 11 to 12 years, but can be given later, at any age.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Human papillomavirus (HPV)",
"       </td>",
"       <td>",
"        3 doses",
"       </td>",
"       <td>",
"        This is usually given at age 11 to 12 years, but can be given from age 9 to 26. People get 3 doses over 6 months.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        11 to 18 years",
"       </td>",
"       <td>",
"        Meningococcal",
"       </td>",
"       <td>",
"        2 doses",
"       </td>",
"       <td>",
"        The first dose is usually given at age 11 to 12 years. A booster dose is usually given at age 16.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        The following vaccines might be given to children with certain conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        7 to 10 years",
"       </td>",
"       <td>",
"        Meningococcal",
"       </td>",
"       <td>",
"        1 dose",
"       </td>",
"       <td>",
"        This is given to children earlier than the recommended age if they have certain medical conditions, or live in or travel to certain places.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        7 to 18 years",
"       </td>",
"       <td>",
"        Pneumococcal",
"       </td>",
"       <td>",
"        1 or 2 doses",
"       </td>",
"       <td>",
"        This is given to children who have certain medical conditions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatitis A (HepA)",
"       </td>",
"       <td>",
"        2 doses",
"       </td>",
"       <td>",
"        Some children who never got this vaccine might need it.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: If your child didn't get all of his or her scheduled vaccines from age 0 to 6 years, he or she might need to get those vaccines from age 7 to 18.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
