var title_f14_4_14412="Diagnostic criteria SUNCT SUNA";
var content_f14_4_14412=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F74387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F74387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for SUNCT and SUNA",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Diagnostic criteria for SUNCT (short lasting unilateral neuralgiform headache attacks with conjunctival injection and tearing)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A. At least 20 attacks fulfilling criteria B-E",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       B. Attacks of unilateral, orbital, supraorbital, or temporal stabbing or pulsating pain last 5 to 240 seconds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       C. Pain is accompanied by ipsilateral conjunctival injection and lacrimation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       D. Attacks occur with a frequency from 3 to 200 per day",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       E. Not attributed to another disorder",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Diagnostic criteria for SUNA (short lasting unilateral neuralgiform headache attacks with cranial autonomic symptoms)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A. At least 20 attacks fulfilling criteria B-E",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       B. Attacks of unilateral orbital, supraorbital or temporal stabbing pain lasting from 2 seconds to 10 minutes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       C. Pain is accompanied by one of:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       1. Conjunctival injection and/or tearing",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       2. Nasal congestion and/or rhinorrhea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       3. Eyelid edema",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       D. Attacks occur with a frequency of &ge;1 per day for more than half the time",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       E. Not attributed to another disorder",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Episodic SUNA:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       SUNA attacks occurring for seven days to one year with pain free intervals longer than one month",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Chronic SUNA:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       At least two attack periods last seven days to one year separated by remission periods of less than one month (untreated)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted by permission of Sage: Headache classification subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24 Suppl 1:9. Copyright &copy; 2004 Sage Publications.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
