var title_f14_4_14413="Wilms tumor staging";
var content_f14_4_14413=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F78599&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F78599&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Staging systems for renal tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        NWTSG (before chemotherapy)",
"       </td>",
"       <td class=\"subtitle1\">",
"        SIOP (after chemotherapy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        I",
"       </td>",
"       <td>",
"        Tumor is limited to the kidney and completely excised",
"       </td>",
"       <td>",
"        Tumor is limited to kidney or surrounded with fibrous pseudocapsule if outside of the normal ontours of the kidney, the renal capsule or pseudocapsule may be infiltrated with the tumor, but it does not reach the outer surface, and is completely resected (resection margins \"clear\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The tumor was not ruptured before or during removal",
"       </td>",
"       <td>",
"        The tumor may be protruding into the pelvic system and \"dipping\" into the ureter (but it is not infiltrating their walls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The vessels of the renal sinus are not involved beyond 2 mm",
"       </td>",
"       <td>",
"        The vessels of the renal sinus are not involved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no residual tumor apparent beyond the margins of excision",
"       </td>",
"       <td>",
"        Intrarenal vessel involvement may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        II",
"       </td>",
"       <td>",
"        Tumor extends beyond the kidney but is completely excised",
"       </td>",
"       <td>",
"        The tumor extends beyond kidney or penetrates through the renal capsule and/or fibrous pseudocapsule into perirenal fat but is completely resected (resection margins \"clear\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No residual tumor is apparent at or beyond the margins of excision",
"       </td>",
"       <td>",
"        The tumor infiltrates the renal sinus and/or invades blood and lymphatic vessels outside the renal parenchyma but is completely resected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumor thrombus in vessels outside the kidney is stage II if the thrombus is removed en bloc with the tumor",
"       </td>",
"       <td>",
"        The tumor infiltrates adjacent organs or vena cava but is completely resected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Although tumor biopsy or local spillage confined to the flank were considered stage II by NWTSG in the past, such events will be considered stage III in upcoming COG studies.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"8\">",
"        <p>",
"         III",
"        </p>",
"        <p>",
"         Residual tumor confined to the abdomen",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lymph nodes in the renal hilum, the periaortic chains, or beyond are found to contain tumor",
"       </td>",
"       <td>",
"        Incomplete excision of the tumor, which extends beyond resection margins (gross or microscopical tumor remains postoperatively)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse peritoneal contamination by the tumor",
"       </td>",
"       <td>",
"        Any abdominal lymph nodes are involved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Implants are found on the peritoneal surfaces",
"       </td>",
"       <td>",
"        Tumor rupture before or intraoperatively (irrespective of other criteria for staging)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumor extends beyond the surgical margins either microscopically or grossly",
"       </td>",
"       <td>",
"        The tumor has penetrated through the peritoneal surface",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumor is not completely resectable because of local infiltration into vital structures",
"       </td>",
"       <td>",
"        Tumor thrombi present at resection margins of vessels or ureter, transsected or removed piecemeal by surgeon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        The tumor has been surgically biopsied (wedge biopsy) prior to preoperative chemotherapy or surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Regional lymph node involvement was considered stage II in the previous SIOP staging system.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Presence of hematogenous metastases or metastases to distant lymph nodes",
"       </td>",
"       <td>",
"        Hematogenous metastases (lung, liver, bone, brain, etc.) or lymph node metastases outside the abdomino-pelvic region",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        V",
"       </td>",
"       <td>",
"        Bilateral renal involvement at the time of initial diagnosis",
"       </td>",
"       <td>",
"        Bilateral renal tumors at diagnosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     COG: Children's Oncology Group; NWTSG: National Wilms Tumor Study Group; SIOP: International Society of Pediatric Oncology.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Metzger ML, Dome JS. Current therapy for Wilms' tumor. Oncologist 2005; 10:815. Copyright &copy;2005 AlphaMed Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
