" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Noncirrhotic portal hypertension: Portal fibrosis and schistosomiasis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/40/14985/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/40/14985/contributors\">",
"     Juan Carlos Garcia-Pagan, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/40/14985/contributors\">",
"     Jason Chang Pik Eu, MBBS, MRCP",
"    </a>",
"    <br/>",
"   </div>",
