" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pharmacology of amphotericin B",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/41/15001/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/41/15001/contributors\">",
"     Richard H Drew, PharmD, MS, BCPS, FCCP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/41/15001/contributors\">",
"     Section Editor",
"    </a>",
